function capitalizeWord(e){return e.toUpperCase()}const lastname=document.querySelector("#lastname");function capitalizeFirstLetter(e){return e.charAt(0).toUpperCase()+e.slice(1)}lastname.addEventListener("keypress",e=>{const t=e.target.value;e.target.value=capitalizeWord(t)});const firstname=document.querySelector("#firstname");firstname.addEventListener("keypress",e=>{const t=e.target.value;e.target.value=capitalizeFirstLetter(t)});var matricule=document.getElementById("matricule"),span=document.getElementById("spanChiffre"),bAjoutAgent=document.getElementById("bAjoutAgent");matricule.addEventListener("input",function(){isNaN(matricule.value)?(span.innerHTML="Veuillez entrer un nombre!",bAjoutAgent.value="prout"):span.innerHTML=""});
const containerTable=document.getElementById("container-table"),form=document.getElementById("form");function toggleForm(){$("#container-table, #form").toggle()}$("#add, #btn-cancel").click(()=>toggleForm());